q = group q by '{{cell(Map_Type_1.selection, 0, "iso code").asString()}}';
q = foreach q generate '{{cell(Map_Type_1.selection, 0, "iso code").asString()}}' as '{{cell(Map_Type_1.selection, 0, "iso code").asString()}}', avg('{{cell(display_selector_1.selection, 0, "Measure3__field").asString()}}') as 'Average';
q2 = foreach q generate "Maximum" as '{{cell(Map_Type_1.selection, 0, "iso code").asString()}}', {{cell(display_selector_1.selection, 0, "high").asString()}} as 'Average';q3 = foreach q generate "Minimum" as '{{cell(Map_Type_1.selection, 0, "iso code").asString()}}', {{cell(display_selector_1.selection, 0, "low").asString()}} as 'Average';result = union q, q2, q3;result = group result by '{{cell(Map_Type_1.selection, 0, "iso code").asString()}}';result = foreach result generate '{{cell(Map_Type_1.selection, 0, "iso code").asString()}}' as '{{cell(Map_Type_1.selection, 0, "iso code").asString()}}', avg('Average') as 'Average Temperature';result = limit result 2000;

